@import url("https://fonts.googleapis.com/css?family=Cousine:400,400i,700,700i");
* {
  box-sizing: border-box; }

body {
  width: 100%;
  height: 100%;
  margin: 0%;
  background-color: #f3f7ff;
  font-family: "Cousine", monospace; }

.gallery {
  display: grid;
  width: 50vw;
  right: 0;
  left: 0;
  margin: auto;
  margin-top: 10vh;
  grid-template-columns: repeat(2, 25vw);
  grid-auto-rows: 20vw;
  grid-gap: 10px; }
  .gallery .project {
    width: 100%;
    height: 100%;
    position: relative;
    object-fit: contain;
    transition: opacity 0.5s ease; }
    .gallery .project .overlay {
      position: absolute;
      width: 100%;
      height: 100%;
      top: 0;
      right: 0;
      opacity: 0;
      transition: opacity 0.5s ease; }
    .gallery .project:hover .overlay {
      opacity: 1; }
    .gallery .project:hover .overlay-txt {
      opacity: 1; }
    .gallery .project img {
      width: 90%;
      height: auto;
      margin-top: 7%;
      margin-left: 5%; }
    .gallery .project .overlay-txt {
      position: absolute;
      width: 70%;
      height: 10%;
      top: 0;
      left: 0;
      bottom: 0;
      right: 0;
      margin: auto;
      transition: 0.5s ease;
      opacity: 0;
      color: white;
      text-align: center; }
      .gallery .project .overlay-txt h2 {
        margin: 0%; }

#logo_container img {
  width: 60%;
  height: auto;
  margin-left: 20%;
  margin-top: 15%;
  margin-bottom: 0%; }

#intro-text {
  grid-column: 2/-1;
  grid-row: 2; }

.nav_link {
  text-decoration: none;
  color: black;
  font-size: 1.8em; }

.nav_link:hover {
  text-decoration: line-through;
  transition: all 1s; }

#about_nav {
  writing-mode: vertical-rl;
  text-orientation: mixed;
  position: fixed;
  right: 2px;
  top: calc(50vh - 2vh); }

#contact_nav {
  writing-mode: vertical-rl;
  text-orientation: mixed;
  position: fixed;
  left: 2px;
  top: 45vh; }

#home_link {
  position: fixed;
  top: 5vh;
  left: 2vw; }

.main-about {
  width: 100vw;
  height: 100vh;
  margin: 0;
  overflow-y: scroll;
  scroll-behavior: smooth; }

.about-me,
.about-skills,
.about-education {
  width: 40vw;
  height: 100vh; }

.about-me h1,
.about-skills h1,
.about-education h1 {
  padding-top: 30%;
  margin-left: 30%; }

.about-me div,
.about-skills div {
  margin-left: 30%;
  padding-top: 20%; }

.about-img {
  position: fixed;
  width: 30%;
  transform: rotate(-90deg);
  top: 25vh;
  right: 15vw;
  border: #3939c6 solid 6px;
  box-shadow: 4px 5px 99px -28px rgba(48, 48, 48, 0.34); }

/*EDUCATION TIMELINE*/
.timeline {
  position: relative;
  width: 100%;
  max-width: 1700px;
  margin: 0 auto;
  margin-left: 30%;
  margin-top: 10%;
  margin-bottom: 20%; }

.timeline::after {
  content: "";
  position: absolute;
  width: 6px;
  height: 50vh;
  background-color: #3d6bc9;
  top: 0;
  bottom: 0;
  left: 10%;
  margin-left: -3px; }

.container {
  padding: 10px 40px;
  position: relative;
  background-color: inherit;
  width: 90%; }

/* The circles on the timeline */
.container::after {
  content: "";
  position: absolute;
  width: 25px;
  height: 25px;
  right: -17px;
  background-color: #f3f7ff;
  border: 4px solid #3939c6;
  top: 15px;
  border-radius: 50%;
  z-index: 1; }

.right {
  left: 10%; }

.right::before {
  content: " ";
  height: 0;
  position: absolute;
  top: 22px;
  width: 0;
  z-index: 1;
  left: 30px;
  border: medium solid black;
  border-width: 10px 10px 10px 0;
  border-color: transparent black transparent transparent; }

/* Fix the circle for containers on the right side */
.right::after {
  left: -16px; }

.content {
  padding: 0px 30px;
  background-color: #f3f7ff;
  position: relative;
  border-radius: 6px; }

.section-links {
  position: fixed;
  right: 6vw;
  top: 16vh; }

.section-links a {
  display: block;
  text-decoration: none;
  color: #3939c6;
  margin-bottom: 10px; }

.section-links a:hover {
  text-decoration: line-through; }

.email-container {
  width: 40vw;
  margin-top: 20vh;
  margin-left: 15%; }

fieldset {
  border: medium none !important;
  margin: 0 0 10px;
  min-width: 100%;
  padding: 0;
  width: 100%; }

#contact {
  background: #f3f7ff;
  padding: 25px;
  margin: 50px 0; }

#contact input[type="text"],
#contact input[type="email"],
#contact input[type="tel"],
#contact input[type="url"],
#contact textarea {
  width: 100%;
  border: 1px solid blue;
  background: #f3f7ff;
  margin: 0 0 5px;
  padding: 10px; }

#contact button[type="submit"] {
  cursor: pointer;
  width: 30%;
  background: #f3f7ff;
  border: none;
  margin: 0 0 5px;
  padding: 10px;
  font-size: 15px; }

.soMe {
  position: absolute;
  right: 20vw;
  width: 8vw;
  height: auto; }

#facebook-link {
  top: 20vh; }

#linkedin-link {
  top: 40vh; }

#github-link {
  top: 60vh; }

.line-through {
  text-decoration: line-through; }
